import { Component } from '@angular/core';
import Swal from 'sweetalert2';
import { Orden } from './orden';
import { OrdenService } from './orden.service';

@Component({
  selector: 'app-orden-ventas',
  templateUrl: './orden-ventas.component.html',
})
export class OrdenVentasComponent {
  titulo = "Ordenes";
  ordenes: Orden[];

  constructor(private ordenService:OrdenService){     
    this.getAll();
  }   

  getAll(): void{
    this.ordenService.getAll().subscribe(
      ordenes => {this.ordenes = ordenes}
    );
  }  

  delete(orden: Orden): void{
    this.ordenService.delete(orden.id).subscribe(
      response => this.ordenService.getAll().subscribe(res => {
        this.ordenes = res
        Swal.fire('Orden eliminado',`Orden ${orden.numero} eliminado con éxito`, 'success');
      })
    );
  }
}
<div class="container"><br>
    <h2 class="mt-4 mb-3" style="font-size: 40px;">Datos de la Orden factura</h2>
    <br>
    <nav aria-label="breadcrumb" style="padding: 0.75rem; background-color: #e9ecef; margin-bottom: 1rem;">
      <ol class="breadcrumb">
        <li class="breadcrumb-item"><a href="#">Home</a></li>
        <li class="breadcrumb-item active" aria-current="page">Carrito</li>
      </ol>
    </nav>

    <div class="card mb-4">
      <div class="card-body">
        <div class="row">
         <div class="col-lg-9">
           <div class="card-body">
            <h5 class="card-title">
              DATOS ORDEN
            </h5>
            <h6>Nombre: </h6>
            <h6 >Correo: </h6>
            <h6 >Dirección</h6>
            <br>
            <h5 class="card-title">
              PRODUCTOS
            </h5>
            <div class="alert alert-light" role="alert">
              <table class="table">
                <thead>
                  <tr>
                    <th scope="col">Producto</th>
                    <th scope="col">Precio</th>
                    <th scope="col">Cantidad</th>
                    <th scope="col">Total</th>
                  </tr>
                </thead>
                <tbody>
                  <tr>
                   <td >Nombre</td>
                   <td >Precio</td>
                   <td>Cantidad</td>
                   <td >Total</td>
                 </tr>
               </tbody>
             </table>
           </div>
         </div>
       </div>
       <div class="col-lg-3">
        <h2 class="card-title">Resumen Orden</h2>

        <ul class="list-group">
          <li class="list-group-item"><h5> Total:</h5></li>
          <a href="ventas/compras" class="btn btn-dark">Generar</a>
        </ul>
      </div>
    </div>
  </div>
</div>
