import { Component } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import Swal from 'sweetalert2';
import { Producto } from './producto';
import { ProductoService } from './producto.service';

@Component({
  selector: 'app-form-producto',
  templateUrl: './form-producto.component.html'
})
export class FormProductoComponent {
  public titulo:string = 'Crear Producto';
  public producto:Producto = new Producto();

  constructor(private productoService:ProductoService, private router:Router, private activatedRoute:ActivatedRoute){
    this.cargarProducto();
  }

cargarProducto(): void{
  this.activatedRoute.params.subscribe(params =>{
    let id = params['id'];
    if(id){
      this.productoService.getProducto(id).subscribe((producto) => this.producto = producto);
    }
  });
}

public create(): void{
    this.productoService.create(this.producto).subscribe(producto => {
      this.router.navigate(['/productos'])
      Swal.fire('Producto guardado',`Producto ${producto.nombre} guardado con éxito`, 'success');
    });
  }
}
<style>  
    .title{
        text-align: center;
        margin-top: 10px;
        font-size: 24px;
        font-weight: bold;
    }    
</style>
<br><br><br>
<div class="container card bg-dark text-white">
    <div class="card-header title">{{titulo}}</div>
    <div class="card-body">
        <form (ngSubmit)="create()" ngNativeValidate>
            <div class="form-group row">
                <label for="" class="col-form-label col-sm-2">Nombre</label>
                <div class="col-sm-6">
                    <input type="text" required class="form-control" name="nombre" [(ngModel)]="producto.nombre">
                </div>
            </div><br>
            <div class="form-group row">
                <label for="" class="col-form-label col-sm-2">Color</label>
                <div class="col-sm-6">
                    <input type="text" required class="form-control" name="color" [(ngModel)]="producto.color">
                </div>
            </div><br>
            <div class="form-group row">
                <label for="" class="col-form-label col-sm-2">Talla</label>
                <div class="col-sm-6">
                    <input type="text" required class="form-control" name="talla" [(ngModel)]="producto.talla">
                </div>
            </div><br>
            <div class="form-group row">
                <label for="categoria" class="col-form-label col-sm-2">Categoría</label>
                <div class="col-sm-6">
                  <select id="categoria" class="form-select" required name="categoria" [(ngModel)]="producto.categoria">
                    <option value="hombre">Hombre</option>
                    <option value="mujer">Mujer</option>
                  </select>
                </div>
              </div><br>
            <div class="form-group row">
                <label for="" class="col-form-label col-sm-2">Precio</label>
                <div class="col-sm-6">
                    <input type="number" step="0.01" required class="form-control" name="precio" [(ngModel)]="producto.precio">
                </div>
            </div><br>
            <div class="form-group row">
                <label for="" class="col-form-label col-sm-2">Stock</label>
                <div class="col-sm-6">
                    <input type="number"  required class="form-control" name="stock" [(ngModel)]="producto.stock">
                </div>
            </div>
            <div class="form-group">           
                <div class="col-sm-10"><br>
                    <label class="control-label col-sm-2" for="img" style="margin-right: 30px;">Imagen:</label>
                  <input type="file" class="form-control-file" id="img" name="img">
                </div>
              </div><br>
            <div class="form-group row" >
                <div class="col-sm-6">
                    <button class="btn btn-primary" role="button">Crear</button>
                </div>
            </div>
        </form>
    </div>
</div>
