import { Component } from '@angular/core';
import Swal from 'sweetalert2';
import { Producto } from './producto';
import { ProductoService } from './producto.service';

@Component({
  selector: 'app-productos',
  templateUrl: './productos.component.html'
})
export class ProductosComponent {
  titulo = "Productos";
  productos: Producto[];

  constructor(private productoService:ProductoService){     
    this.getAll();
  }   

  getAll(): void{
    this.productoService.getAll().subscribe(
      productos => {this.productos = productos}
    );
  }  

  delete(producto: Producto): void{
    this.productoService.delete(producto.id).subscribe(
      response => this.productoService.getAll().subscribe(res => {
        this.productos = res
        Swal.fire('Producto eliminado',`Producto ${producto.nombre} eliminado con éxito`, 'success');
      })
    );
  }
}

<div class="container"><br><br>
    <h2 class="text-center" style="font-size: 40px;">{{ titulo }}</h2><br><br>
    <div class="card border-primary mb-3">
      <div class="card-header" style="background-color: #e9ecef;">PRODUCTOS</div>
      <div class="card-body text-primary">
        <div class="col-sm-6">
          <button class="btn btn-primary" role="button" [routerLink]="['form-productos']">Agregar</button>
        </div><br>
        <h5 class="card-title" style="margin-bottom: 16px;">Listado de Productos</h5>      
        <table class="table table-bordered table-striped">
          <thead>
              <tr>
                  <th>id</th>
                  <th>nombre</th>
                  <th>color</th>
                  <th>talla</th>
                  <th>precio</th>
                  <th>stock</th>        
                  <th>modificar</th>
                  <th>eliminar</th>
              </tr>
          </thead>
          <tbody>
              <tr *ngFor="let producto of productos">
                  <td>{{producto.id}}</td>
                  <td>{{producto.nombre}}</td>
                  <td>{{producto.color}}</td>
                  <th>{{producto.talla}}</th>
                  <td>{{producto.precio}}</td>
                  <td>{{producto.stock}}</td>                  
                  <td>
                    <button class="btn btn-primary" type="button" name="button" [routerLink]="['form-productos',producto.id]">Editar</button>
                  </td>
                  <td>
                    <button class="btn btn-primary" type="button" name="button" (click)="delete(producto)">Eliminar</button>
                  </td>
              </tr>
          </tbody>
        </table>      
      </div>
    </div>
  </div>
