import { Component, ElementRef, HostListener } from '@angular/core';

@Component({
  selector: 'app-footer',
  templateUrl: './footer.component.html',
  styleUrls: ['./footer.component.css']
})
export class FooterComponent {
  autor: any = 'Vuelo 93';
  hideFooter: boolean = false;

  constructor(private elementRef: ElementRef) {}

  @HostListener('window:scroll', ['$event'])
  onScroll(event: any) {
    const windowHeight = window.innerHeight;
    const bodyHeight = this.elementRef.nativeElement.offsetHeight;
    const scrollHeight = window.pageYOffset;

    // nueva comprobación para detectar el final de la página
    const atBottom = scrollHeight + windowHeight === bodyHeight;

    this.hideFooter = windowHeight + scrollHeight >= bodyHeight && !atBottom;
  }
}
