import { HttpClient, HttpHeaders } from "@angular/common/http";
import { Injectable } from "@angular/core";
import { map, Observable } from "rxjs";
import { Producto } from "./producto";

@Injectable({
    providedIn: 'root'
  })
  export class ClienteService {
  
    private urlEndPoint:string = "http://localhost:8080/api/productos";
    private httpHeaders = new  HttpHeaders({'Content-Type': 'Application/json'});
  
    constructor(private http:HttpClient) { }
  
    // Listar clientes del backend
    getAll() : Observable<Producto[]> {    
      return this.http.get(this.urlEndPoint).pipe(
        map(response => response as Producto[])
      );
    }
    
    //obtener el id del cliente
    getCliente(id:any): Observable<Producto> {
      return this.http.get<Producto>(`${this.urlEndPoint}/${id}`);
    }
  
    // Crear cliente en el backend
    create(cliente: Producto): Observable<Producto> {
      return this.http.post<Producto>(this.urlEndPoint, cliente, {headers: this.httpHeaders});
    }
  
    // Eliminar cliente en el backend
    delete(id:any): Observable<Producto> {
      return this.http.delete<Producto>(`${this.urlEndPoint}/${id}`);
    }
  
  
  }
