import { Component } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import Swal from 'sweetalert2';
import { Categoria } from './categoria';
import { CategoriaService } from './categoria.service';

@Component({
  selector: 'app-form-categoria',
  templateUrl: './form-categoria.component.html'
})
export class FormCategoriaComponent {

  public titulo:string = 'Crear Categoria';
  public categoria:Categoria = new Categoria();

  constructor(private categoriaService:CategoriaService, private router:Router, private activatedRoute:ActivatedRoute){
    this.cargarCategoria();
  }

  cargarCategoria(): void{
    this.activatedRoute.params.subscribe(params =>{
      let id = params['id'];
      if(id){
        this.categoriaService.getCategoria(id).subscribe((categoria) => this.categoria = categoria);
      }
    });
  }

  public create(): void{
      this.categoriaService.create(this.categoria).subscribe(categoria => {
        this.router.navigate(['/categorias'])
        Swal.fire('Categoria guardado',`Categoria ${categoria.genero} guardado con éxito`, 'success');
      });
  }

}
<style>  
    .title{
        text-align: center;
        margin-top: 10px;
        font-size: 24px;
        font-weight: bold;
    }    
</style>
<br><br><br>
<div class="container card bg-dark text-white">
    <div class="card-header title">{{titulo}}</div>
    <div class="card-body">
        <form (ngSubmit)="create()" ngNativeValidate>
            <div class="form-group row">
                <label for="" class="col-form-label col-sm-2">Genero</label>
                <div class="col-sm-6">
                    <input type="number"  required class="form-control" name="genero" [(ngModel)]="categoria.genero">
                </div>
            </div><br>
            <div class="form-group row" >
                <div class="col-sm-6">
                    <button class="btn btn-primary" role="button">Crear</button>
                </div>
            </div>
        </form>
    </div>
</div>
